import React, { useState } from 'react';
import './App.css';

function App() {
  const [teams, setTeams] = useState([
    { name: 'Time A', points: 0 },
    { name: 'Time B', points: 0 },
    { name: 'Time C', points: 0 },
    { name: 'Time D', points: 0 },
  ]);

  const [error, setError] = useState('');
  const [semifinal1, setSemifinal1] = useState('');
  const [semifinal2, setSemifinal2] = useState('');
  const [finalTeams, setFinalTeams] = useState({ semifinal1: '', semifinal2: '' });

  const handleMatchResult = (team1, team2, winner) => {
    setTeams(teams.map(team => {
      if (team.name === winner) {
        return { ...team, points: team.points + 3 };
      }
      return team;
    }));
  };

  const validateTeams = (team1, team2, semifinal1, semifinal2) => {
    if (team1 === team2) {
      setError("Selecione times diferentes para o Jogo 1 e Jogo 2.");
      return false;
    }
    if (semifinal1 === semifinal2) {
      setError("Selecionado o mesmo time para as semifinais.");
      return false;
    }
    setError("");
    return true;
  };

  const handleSubmitSemifinals = (e) => {
    e.preventDefault();
    if (semifinal1 && semifinal2 && semifinal1 !== semifinal2) {
      setFinalTeams({ semifinal1, semifinal2 });
      setError('');
    } else {
      setError('Selecione semifinais válidas.');
    }
  };

  return (
    <div className="App">
      <h1>Campeonato de Pontos Corridos</h1>
      <SemifinalForm 
        teams={teams} 
        semifinal1={semifinal1} 
        semifinal2={semifinal2}
        setSemifinal1={setSemifinal1} 
        setSemifinal2={setSemifinal2} 
        handleSubmit={handleSubmitSemifinals}
        setError={setError}
      />
      {error && <div className="error">{error}</div>}
      <MatchResultForm 
        onMatchResult={handleMatchResult} 
        teams={teams} 
        finalTeams={finalTeams} 
        setError={setError}
      />
      <Table teams={teams} />
    </div>
  );
}

function MatchResultForm({ onMatchResult, teams, finalTeams, setError }) {
  const [team1, setTeam1] = useState('');
  const [team2, setTeam2] = useState('');
  const [winner, setWinner] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (finalTeams.semifinal1 && finalTeams.semifinal2) {
      if (team1 && team2 && winner) {
        if (winner === finalTeams.semifinal1 || winner === finalTeams.semifinal2) {
          onMatchResult(team1, team2, winner);
          setTeam1('');
          setTeam2('');
          setWinner('');
          setError(''); // Clear error if successful
        } else {
          setError('O vencedor deve ser um dos times que ganharam as semifinais.');
        }
      } else {
        setError('Preencha todos os campos do formulário.');
      }
    } else {
      setError('Semifinais ainda não definidas.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Adicionar Resultado do Jogo</h2>
      <div>
        <label>
          Time 1:
          <select value={team1} onChange={(e) => setTeam1(e.target.value)}>
            <option value="">Selecione</option>
            {teams.map(team => (
              <option key={team.name} value={team.name}>{team.name}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Time 2:
          <select value={team2} onChange={(e) => setTeam2(e.target.value)}>
            <option value="">Selecione</option>
            {teams.map(team => (
              <option key={team.name} value={team.name}>{team.name}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Vencedor:
          <select value={winner} onChange={(e) => setWinner(e.target.value)}>
            <option value="">Selecione</option>
            {finalTeams.semifinal1 && (
              <option value={finalTeams.semifinal1}>{finalTeams.semifinal1}</option>
            )}
            {finalTeams.semifinal2 && (
              <option value={finalTeams.semifinal2}>{finalTeams.semifinal2}</option>
            )}
          </select>
        </label>
      </div>
      <button type="submit">Adicionar Resultado</button>
    </form>
  );
}

function SemifinalForm({ teams, semifinal1, semifinal2, setSemifinal1, setSemifinal2, handleSubmit, setError }) {
  return (
    <form onSubmit={handleSubmit}>
      <h2>Definir Semifinais</h2>
      <div>
        <label>
          Semifinal 1:
          <select value={semifinal1} onChange={(e) => setSemifinal1(e.target.value)}>
            <option value="">Selecione</option>
            {teams.map(team => (
              <option key={team.name} value={team.name}>{team.name}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Semifinal 2:
          <select value={semifinal2} onChange={(e) => setSemifinal2(e.target.value)}>
            <option value="">Selecione</option>
            {teams.map(team => (
              <option key={team.name} value={team.name}>{team.name}</option>
            ))}
          </select>
        </label>
      </div>
      <button type="submit">Definir Semifinais</button>
    </form>
  );
}

function Table({ teams }) {
  // Função para formatar pontos como moeda
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(amount);
  };

  return (
    <div>
      <h2>Tabela de Pontos</h2>
      <table>
        <thead>
          <tr>
            <th>Time</th>
            <th>Pontos</th>
          </tr>
        </thead>
        <tbody>
          {teams
            .slice()
            .sort((a, b) => b.points - a.points)
            .map(team => (
              <tr key={team.name}>
                <td>{team.name}</td>
                <td>{formatCurrency(team.points)}</td> {/* Formata pontos como moeda */}
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
