import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const UserPage = ({ users, addNote, removeNote }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const user = users.find(user => user.id === parseInt(id));

  if (!user) {
    return <p>Usuário não encontrado</p>;
  }

  const handleAddNote = () => {
    const note = prompt('Digite a nota:');
    if (note) {
      addNote(user.id, note);
    }
  };

  const handleRemoveNote = (index) => {
    removeNote(user.id, index);
  };

  return (
    <div>
      <h1>{user.name}</h1>
      <button onClick={handleAddNote}>Adicionar Nota</button>
      <ul>
        {user.notes.map((note, index) => (
          <li key={index}>
            {note} <button onClick={() => handleRemoveNote(index)}>Remover</button>
          </li>
        ))}
      </ul>
      <button onClick={() => navigate('/')}>Voltar para a lista</button>
    </div>
  );
};

export default UserPage;
