import React, { useState } from 'react';
import './App.css';

function App() {
  const [teams, setTeams] = useState([
    { name: 'Time A', points: 0 },
    { name: 'Time B', points: 0 },
    { name: 'Time C', points: 0 },
    { name: 'Time D', points: 0 },
  ]);

  const [error, setError] = useState('');

  const handleMatchResult = (team1, team2, winner) => {
    setTeams(teams.map(team => {
      if (team.name === winner) {
        return { ...team, points: team.points + 3 };
      }
      return team;
    }));
  };

  const validateTeams = (team1, team2, semifinal1, semifinal2) => {
    if (team1 === team2) {
      setError("Selecione times diferentes para o Jogo 1 e Jogo 2.");
      return false;
    }
    if (semifinal1 === semifinal2) {
      setError("Selecionado o mesmo time para as semifinais.");
      return false;
    }
    setError("");
    return true;
  };

  return (
    <div className="App">
      <h1>Campeonato de Pontos Corridos</h1>
      <MatchResultForm 
        onMatchResult={handleMatchResult} 
        teams={teams} 
        validateTeams={validateTeams} 
        setError={setError}
      />
      {error && <div className="error">{error}</div>}
      <Table teams={teams} />
    </div>
  );
}

function MatchResultForm({ onMatchResult, teams, validateTeams, setError }) {
  const [team1, setTeam1] = useState('');
  const [team2, setTeam2] = useState('');
  const [winner, setWinner] = useState('');
  const [semifinal1, setSemifinal1] = useState('');
  const [semifinal2, setSemifinal2] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateTeams(team1, team2, semifinal1, semifinal2)) {
      if (team1 && team2 && winner) {
        onMatchResult(team1, team2, winner);
        setTeam1('');
        setTeam2('');
        setWinner('');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Adicionar Resultado do Jogo</h2>
      <div>
        <label>
          Time 1:
          <select value={team1} onChange={(e) => setTeam1(e.target.value)}>
            <option value="">Selecione</option>
            {teams.map(team => (
              <option key={team.name} value={team.name}>{team.name}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Time 2:
          <select value={team2} onChange={(e) => setTeam2(e.target.value)}>
            <option value="">Selecione</option>
            {teams.map(team => (
              <option key={team.name} value={team.name}>{team.name}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Vencedor:
          <select value={winner} onChange={(e) => setWinner(e.target.value)}>
            <option value="">Selecione</option>
            {teams.map(team => (
              <option key={team.name} value={team.name}>{team.name}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Semifinal 1:
          <select value={semifinal1} onChange={(e) => setSemifinal1(e.target.value)}>
            <option value="">Selecione</option>
            {teams.map(team => (
              <option key={team.name} value={team.name}>{team.name}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Semifinal 2:
          <select value={semifinal2} onChange={(e) => setSemifinal2(e.target.value)}>
            <option value="">Selecione</option>
            {teams.map(team => (
              <option key={team.name} value={team.name}>{team.name}</option>
            ))}
          </select>
        </label>
      </div>
      <button type="submit">Adicionar Resultado</button>
    </form>
  );
}

function Table({ teams }) {
  return (
    <div>
      <h2>Tabela de Pontos</h2>
      <table>
        <thead>
          <tr>
            <th>Time</th>
            <th>Pontos</th>
          </tr>
        </thead>
        <tbody>
          {teams
            .slice()
            .sort((a, b) => b.points - a.points)
            .map(team => (
              <tr key={team.name}>
                <td>{team.name}</td>
                <td>{team.points}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
