// src/Torneio.js
import React, { useState } from 'react';

const Torneio = () => {
  // Estados para armazenar os times e resultados
  const [times, setTimes] = useState(['Time A', 'Time B', 'Time C', 'Time D']);
  const [resultados, setResultados] = useState({});

  // Função para gerar um resultado aleatório
  const gerarResultado = () => Math.floor(Math.random() * 2) === 0 ? 'Time A' : 'Time B';

  // Função para simular o torneio
  const simularTorneio = () => {
    const [time1, time2] = [times[0], times[1]];
    const [time3, time4] = [times[2], times[3]];

    // Simula as semifinais
    const semifinal1 = gerarResultado();
    const semifinal2 = gerarResultado();

    // Simula a final
    const final = gerarResultado();

    setResultados({
      semifinal1: `${time1} vs ${time2} - Vencedor: ${semifinal1}`,
      semifinal2: `${time3} vs ${time4} - Vencedor: ${semifinal2}`,
      final: `Final: ${semifinal1} vs ${semifinal2} - Vencedor: ${final}`,
    });
  };

  return (
    <div>
      <h1>Torneio Mata-Mata</h1>
      <button onClick={simularTorneio}>Simular Torneio</button>
      <div>
        <h2>Resultados</h2>
        <p><strong>Semifinal 1:</strong> {resultados.semifinal1 || 'N/A'}</p>
        <p><strong>Semifinal 2:</strong> {resultados.semifinal2 || 'N/A'}</p>
        <p><strong>Final:</strong> {resultados.final || 'N/A'}</p>
      </div>
    </div>
  );
};

export default Torneio;
