// src/Torneio.js
import React, { useState } from 'react';

// Função para gerar um vencedor aleatório entre dois times
const gerarVencedor = (time1, time2) => {
  return Math.random() < 0.5 ? time1 : time2;
};

const Torneio = () => {
  // Estados para armazenar os times e resultados
  const [times] = useState(['Time A', 'Time B', 'Time C', 'Time D']);
  const [resultados, setResultados] = useState({});

  // Função para simular o torneio
  const simularTorneio = () => {
    const [time1, time2, time3, time4] = times;

    // Simula as semifinais
    const vencedorSemifinal1 = gerarVencedor(time1, time2);
    const vencedorSemifinal2 = gerarVencedor(time3, time4);

    // Simula a final
    const vencedorFinal = gerarVencedor(vencedorSemifinal1, vencedorSemifinal2);

    setResultados({
      semifinal1: `${time1} vs ${time2} - Vencedor: ${vencedorSemifinal1}`,
      semifinal2: `${time3} vs ${time4} - Vencedor: ${vencedorSemifinal2}`,
      final: `Final: ${vencedorSemifinal1} vs ${vencedorSemifinal2} - Vencedor: ${vencedorFinal}`,
    });
  };

  return (
    <div>
      <h1>Torneio Mata-Mata</h1>
      <button onClick={simularTorneio}>Simular Torneio</button>
      <div>
        <h2>Resultados</h2>
        <p><strong>Semifinal 1:</strong> {resultados.semifinal1 || 'N/A'}</p>
        <p><strong>Semifinal 2:</strong> {resultados.semifinal2 || 'N/A'}</p>
        <p><strong>Final:</strong> {resultados.final || 'N/A'}</p>
      </div>
    </div>
  );
};

export default Torneio;
