import React, { useState } from 'react';
import './App.css';

// Defina os times e os jogos
const initialTeams = [
  { id: 1, name: 'Time 1' },
  { id: 2, name: 'Time 2' },
  { id: 3, name: 'Time 3' },
  { id: 4, name: 'Time 4' },
];

const generateMatches = (teams) => {
  const matches = [];
  for (let i = 0; i < teams.length; i += 2) {
    matches.push({ team1: teams[i], team2: teams[i + 1] });
  }
  return matches;
};

const App = () => {
  const [teams, setTeams] = useState(initialTeams);
  const [matches, setMatches] = useState(generateMatches(initialTeams));
  const [round, setRound] = useState(1);

  const handleResult = (team1Id, team2Id, winnerId) => {
    // Encontrar o time vencedor
    const winner = teams.find(t => t.id === winnerId);
    if (!winner) return;

    // Atualizar os times
    const updatedTeams = teams.filter(t => t.id === winnerId);

    // Se o número de times for 1, temos um campeão
    if (updatedTeams.length === 1) {
      setTeams(updatedTeams);
      setMatches([]);
      return;
    }

    // Preparar para a próxima rodada
    const nextRoundTeams = [...updatedTeams];
    const nextRoundMatches = generateMatches(nextRoundTeams);

    setTeams(nextRoundTeams);
    setMatches(nextRoundMatches);
    setRound(round + 1);
  };

  const renderMatches = () => {
    return matches.map((match, index) => (
      <div key={index} className="match">
        <span>{match.team1.name} vs {match.team2.name}</span>
        <button onClick={() => handleResult(match.team1.id, match.team2.id, match.team1.id)}>Time 1 Wins</button>
        <button onClick={() => handleResult(match.team1.id, match.team2.id, match.team2.id)}>Time 2 Wins</button>
      </div>
    ));
  };

  const renderChampion = () => {
    if (teams.length === 1) {
      return <h1>Campeão: {teams[0].name}</h1>;
    }
    return null;
  };

  return (
    <div className="App">
      <h1>Campeonato Mata-Mata</h1>
      <h2>Rodada {round}</h2>
      {renderMatches()}
      {renderChampion()}
    </div>
  );
};

export default App;
