import React from 'react';

// Dados dos clientes
const clientes = [
    {
        codCliente: "49079 - TILIM COMERCIO DE ALIMENTOS LTDA",
        supervisorRegional: "ANDRE MARTINS",
        dtUltComp: "31/07/2024",
        pvTotal: 58263.64
    },
    // ... outros clientes
];

function App() {
    // Obtendo o primeiro cliente
    const primeiroCliente = clientes[0];

    // Função para formatar valores como moeda
    const formatCurrency = (value) => {
        return value.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' });
    };

    // Dados fictícios de equipes para a tabela (para ilustrar)
    const teams = [
        { name: 'Time A', points: 1500 },
        { name: 'Time B', points: 1200 },
        { name: 'Time C', points: 1000 },
    ];

    return (
        <div>
            <h1>Informações do Primeiro Cliente</h1>
            <div>
                <h2>Cliente: {primeiroCliente.codCliente}</h2>
                <p><strong>Supervisor Regional:</strong> {primeiroCliente.supervisorRegional}</p>
                <p><strong>Data da Última Compra:</strong> {primeiroCliente.dtUltComp}</p>
                <p><strong>Valor Total:</strong> {formatCurrency(primeiroCliente.pvTotal)}</p>
            </div>

            <h2>Tabela de Pontos</h2>
            <table>
                <thead>
                    <tr>
                        <th>Time</th>
                        <th>Pontos</th>
                    </tr>
                </thead>
                <tbody>
                    {teams
                        .slice()
                        .sort((a, b) => b.points - a.points)
                        .map((team) => (
                            <tr key={team.name}>
                                <td>{team.name}</td>
                                <td>{formatCurrency(team.points)}</td>
                            </tr>
                        ))}
                </tbody>
            </table>
        </div>
    );
}

export default App;
